const images = [
    "https://content.paulreiffer.com/wp-content/uploads/2021/04/Commercial-Travel-Destination-Images-Paul-Reiffer-Licensing-Photography-Photographer-New-Zealand-Tasman-Lake-Copyright-Banner-Image.jpg",
    "https://preview.redd.it/4k-wallpaper-santorini-greece-europe-v0-iz8uca059bib1.jpg?width=1080&crop=smart&auto=webp&s=a5a62cfd02d2df13985bdea9b75fdcffa8bdb7be",
    "https://nariatravel.gr/wp-content/uploads/2017/09/evening-travel-wallpaper-hd-download-free-background-images-amazing-high-definition-4k-2560x1600.jpg",
    "https://c4.wallpaperflare.com/wallpaper/161/296/845/nature-sea-water-turquoise-wallpaper-preview.jpg",
    "https://static.toiimg.com/thumb/msid-92089121,width-748,height-499,resizemode=4,imgsize-139308/.jpg",
    "https://static.toiimg.com/thumb/msid-79490527,width-748,height-499,resizemode=4,imgsize-229216/.jpg"
];

let currentIndex = 0;

function changeImage() {
    const imageElement = document.getElementById("imageElement");
    imageElement.classList.add("vortex-animation");
    
    setTimeout(() => {
        currentIndex = (currentIndex + 1) % images.length;
        imageElement.src = images[currentIndex]; 
        imageElement.classList.remove("vortex-animation");
    }, 1000);
}

function showMainContent() {
    document.querySelector('.container').style.opacity = 0;
    document.querySelector('.container').style.transition = 'opacity 1s ease-out';
    setTimeout(() => {
        document.querySelector('.container').style.display = 'none';
        document.querySelector('.main-content').style.display = 'block';
        document.querySelector('.main-content').style.opacity = 0;
        document.querySelector('.main-content').style.transition = 'opacity 1s ease-in';
        setTimeout(() => {
            document.querySelector('.main-content').style.opacity = 1;
        }, 50);
    }, 1000);
}

const destinations = [
    { name: 'Paris', image: 'data:image/jpeg;base64,' },
    { name: 'Tokyo', image: 'data:image/jpeg;base64,' },
    { name: 'New York', image: 'https://media.nomadicmatt.com/2022/nycguide2a.jpeg' },
    { name: 'Rome', image: 'data:image/jpeg;base64,' },
    { name: 'Sydney', image: 'https://static.toiimg.com/photo/58515800.cms' },
    { name: 'Bali', image: 'https://www.oyorooms.com/blog/wp-content/uploads/2019/04/shutterstock_631736717.jpg' },
    { name: 'Singapore', image: 'https://media.easemytrip.com/media/Deal/DL638423908772645185/SightSeeing/SightSeeingOLewcq.jpg' },
];

function createDestinationCarousel() {
    const track = document.querySelector('.carousel-track');
    let currentIndex = 0;

    destinations.forEach((dest, index) => {
        const polaroid = document.createElement('div');
        polaroid.className = 'polaroid';
        polaroid.innerHTML = `
            <img src="${dest.image}" alt="${dest.name}">
            <p>${dest.name}</p>
        `;
        track.appendChild(polaroid);
    });

    function updateCarousel() {
        const slideWidth = document.querySelector('.polaroid').offsetWidth + 20; // 20 for margin
        track.style.transform = `translateX(-${currentIndex * slideWidth}px)`;
    }

    document.querySelector('.carousel-button.prev').addEventListener('click', () => {
        currentIndex = (currentIndex - 1 + destinations.length) % destinations.length;
        updateCarousel();
    });

    document.querySelector('.carousel-button.next').addEventListener('click', () => {
        currentIndex = (currentIndex + 1) % destinations.length;
        updateCarousel();
    });

    updateCarousel();
}

window.addEventListener('load', () => {
    setTimeout(() => {
        showMainContent();
        createDestinationCarousel();
    }, 3000);
    changeImage();
    setInterval(changeImage, 4500);
});